sql.if:
and publication.publication_type_id in (select publication_type_id from publication_type where
upper(name) like '%' || upper(#{r}) || '%'
)

and publication.publication_subtype_id in (select publication_subtype_id from publication_subtype where
upper(name) like '%' || upper(#{r}) || '%'
)

and publication.publication_series_id in (select publication_series_id from publication_series where
upper(name) like '%' || upper(#{r}) || '%'
)
